var responsive = {};
function initCarousel(){
    if($('.slider-block').hasClass('slider-block_opened')){
        responsive = {
            0:{
                items:1
            },
            600:{
                items:1
            },
            800:{
                items:3
            },
            1000:{
                items:3
            },
            1200:{
                items:3
            }
        };
    }else{
        responsive = {
            0:{
                items:1
            },
            600:{
                items:3
            },
            1000:{
                items:3
            },
            1200:{
                items:($('.slider__item').length > 3) ? 5 : 3
            }
        };
    }

    try{
        var conf__items = JSON.parse(owl.data('config'));
    }catch (e) {
        var conf__items = {};
    }

    var r = owl.data('responsive');
    if(r != undefined){
        responsive = r
    }

    var config = {
        nav:(conf__items.nav != undefined) ? conf__items.nav : true,
        //loop:(conf__items.loop != undefined) ? conf__items.loop : true,
        responsive:responsive,
        center:(conf__items.center != undefined) ? conf__items.center : true,
        navElement:(conf__items.navElement != undefined) ? conf__items.navElement : "slider__arrow__wrapper",
        dots:(conf__items.dots != undefined) ? conf__items.dots : false,
        autoWidth: (conf__items.autoWidth != undefined) ? conf__items.autoWidth : false,
    }

    owl.owlCarousel(config);

    if(!config.nav){
        $('.disabled').css({'display':'none'});
    }
}

var owl = $('.owl-slider');
$(document).on('click','.owl-item', function(e){
    if(!$(this).hasClass('center')){
        var current = $('.owl-item.center').index();
        var n = $(this).index();
        var ww = window.innerWidth;
        var prevent__num = false;
        var index = 0;
        for(var num in responsive){
            if(prevent__num){
                if(ww >= prevent__num && ww <= num){
                    var index = prevent__num;
                    break;
                }else if(ww <= prevent__num && ww >= num){
                    var index = num;
                    break;
                }
            }
            prevent__num = num;
        }
        owl.trigger('to.owl.carousel',[n]);
    }else if($(this).hasClass('center')){

        var slide = $(this).find('.slider__item');
        var func__name = 'open__or__close' + slide.data('activated');

        window[func__name](slide);

    }
})



function open__or__closeProfile(item){

    document.body.style.overflowY = 'hidden';
    $('html').css({'overflow-y':'hidden'});

    var slide = item,
        open = slide.data('open'),
        category = slide.data('category');

    if(open == 0){
        if(findOpenCategory()){
            hideAllWithoutAnimSlider();
            slide.data('open',1);
            openCategory(category);
        }else{
            slide.data('open',1);
            openCategory(category);
        }
    }else{
        slide.data('open',0);
        closeCategory(category)
    }
    initCarousel();
}

function findOpenCategory(){
    var searched = false;
    $('.slider__item').each(function(index, item){
        if($(item).data('open') == 1) searched = true
    });
    return searched;
}

function getNumber(current, target){
    return (target > current) ? target + 2 : target - 3;
}




function getCategory(cat){
    $('.category').each(function (index, item) {
        var it = $(item);
        if (it.data('category') == cat) {
            it.removeClass('dn');
            setTimeout(function(){
                it.removeClass('category_hidden');
                setTimeout(function () {
                    it.removeClass('pa');

                }, 50)
            },50);
        }
    });
    setTimeout(function(){
        $('.slider-block').addClass('slider-block_opened');
        $('.slider-block').addClass('delay__width');
        setTimeout(function () {
            //owl.trigger('refresh.owl.carousel');
            document.body.style.overflowY = 'auto';
            $('html').css({'overflow-y':'auto'});
        },500);
    },50)


}

function hideAllCategory() {
    $('.slider__item').each(function(index, item){
        $(item).data('open',0);
    });
    $('.category').each(function(index,item){
        var it = $(item);
        it.addClass('pa');
        setTimeout(function(){
            it.addClass('category_hidden');
            setTimeout(function(){
                it.addClass('dn')
                $('.slider-block').removeClass('delay__width');
                $('.slider-block').removeClass('slider-block_opened');
                $('.owl-stage').removeClass('owl-auto');
            },200)
        },100);
    })
    setTimeout(function(){
        setTimeout(function () {
            //owl.trigger('refresh.owl.carousel');
            document.body.style.overflowY = 'auto';
            $('html').css({'overflow-y':'auto'});
        },600);
    },50);

}


function hideAllWithoutAnimSlider(){
    $('.slider__item').each(function(index, item){
        $(item).data('open',0);
    });
    $('.category').each(function(index,item){
        var it = $(item);
        it.addClass('pa');
        setTimeout(function(){
            it.addClass('category_hidden');
            setTimeout(function(){
                it.addClass('dn')
                document.body.style.overflowY = 'auto';
                $('html').css({'overflow-y':'auto'});
            },200)
        },100);
    })
}

function openCategory(categoryName){
    $('.slider-block').addClass('slider-block_opened');
    setTimeout(function(){
        $('.slider__item').addClass('slider__item_up');
        setTimeout(function(){
            $('.owl-stage').addClass('owl-auto');
            getCategory(categoryName);
        },100);
    },300);

}



function closeCategory(categoryName){

    setTimeout(function(){
        $('.slider__item').removeClass('slider__item_up');
        setTimeout(function(){
            hideAllCategory();
        },100);
    },300)

}

